Board.prototype.sendI2CReadRequest = function(address, numBytes, callback) {
  this.sp.write(
    new Buffer([
      START_SYSEX,
      I2C_REQUEST,
      address,
      this.I2C_MODES.READ << 3,
      numBytes & 0x7F, (numBytes >> 7) & 0x7F,
      END_SYSEX
    ])
  );
  this.once("I2C-reply-" + address + "-0" , callback);
};

